import React, { useEffect, useState } from "react";
import { Line } from "react-chartjs-2";
import "chart.js/auto"; // Importerer automatisk nødvendig Chart.js-konfigurasjon

const StrompriserDiagram = ({ apiData }) => {
  const [chartData, setChartData] = useState(null);

  useEffect(() => {
    if (apiData) {
      const labels = apiData.map((entry) =>
        new Date(entry.time_start).toLocaleTimeString("no-NO", {
          hour: "2-digit",
          minute: "2-digit",
        })
      );
      const prices = apiData.map((entry) => entry.NOK_per_kWh);

      setChartData({
        labels,
        datasets: [
          {
            label: "Strømpris (NOK/kWh)",
            data: prices,
            borderColor: "rgba(75, 192, 192, 1)",
            backgroundColor: "rgba(75, 192, 192, 0.2)",
            fill: true,
          },
        ],
      });
    }
  }, [apiData]);

  if (!chartData) return <p>Laster strømpriser...</p>;

  return (
    <div style={{ width: "80%", margin: "0 auto" }}>
      <h2>Dagens strømpriser</h2>
      <Line data={chartData} />
    </div>
  );
};

export default StrompriserDiagram;
